  vpc_id      = var.vpc_id

  ingress {
    description              = "Allow traffic from ALB on port 3000"
    from_port                = 3000
    to_port                  = 3000
    protocol                 = "tcp"
    security_groups          = [aws_security_group.alb_security_group.id]
  }

  ingress {
    description              = "Allow traffic from ALB on port 3001"
    from_port                = 3001
    to_port                  = 3001
    protocol                 = "tcp"
    security_groups          = [aws_security_group.alb_security_group.id]
  }

  egress {
    from_port   = 0
    to_port     = 0
    protocol    = "-1"
    cidr_blocks = ["0.0.0.0/0"]
  }
}

